import React from 'react'

class Index extends React.Component{
    constructor(){
        super();
        this.state = {
            loginForm: false,
            signinForm: false
        }
    }

    loginForm = (e) => {
        e.stopPropagation();
    }

    render(){
        return( 
            <>
            <button className="loginButton" onClick={()=>this.setState({loginForm: true})}>
            {this.state.loginForm? 
            <div className="loginForm" onClick={this.loginForm}>
                Username<br/>
                <input type="text" placeholder="Enter Username"/><br/>
                Password<br/>
                <input type="password" placeholder="Enter Password"/><br/>
                <div className="close-container" onClick={()=>this.setState({loginForm: false})}>
                <div className="leftright"></div>
                <div className="rightleft"></div>
                <label className="close">close</label>
                </div>
            </div>
            : "Login to Start"}
            </button>
            <br/>
            <button className="signupButton" onClick={()=>this.setState({signinForm: !this.state.signinForm})}>
            {this.state.signinForm?
            <div className="signupForm" onClick={this.signinForm}>
                Username: <input type="text" placeholder="Enter Username"/>
                Password: <input type="password" placeholder="Enter Password"/>
                <div className="close-container">
                <div className="leftright"></div>
                <div className="rightleft"></div>
                <label className="close">close</label>
                </div>
            </div>
            : "Sign up"}
            </button>
            </>
        );
    }
}

export default Index;
